     // ------------------- MODAL CONTROLS ----------------------
    document.getElementById("openBanquet").onclick = () => {
        document.getElementById("banquetModal").style.display = "flex";
    };
    document.getElementById("closeBanquet").onclick = () => {
        document.getElementById("banquetModal").style.display = "none";
    };

    document.getElementById("openUser").onclick = () => {
        document.getElementById("userModal").style.display = "flex";
    };
    document.getElementById("closeUser").onclick = () => {
        document.getElementById("userModal").style.display = "none";
    };